import React, { useRef, useEffect } from 'react';
import {
  StyleSheet,
  Text,
  View,
  ScrollView,
  Dimensions,
  Animated,
  Platform,
} from 'react-native';
import { StatusBar } from 'expo-status-bar';
import { LinearGradient } from 'expo-linear-gradient';

const { width } = Dimensions.get('window');

// ‚îÄ‚îÄ Category data ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const CATEGORIES = [
  {
    id: 'mowers',
    title: 'Mowers',
    icon: 'üöú',
    tagline: 'Find the perfect cut for Bermuda',
    description:
      'Reel mowers, rotary mowers, and robotic options ‚Äî each suited for different Bermuda varieties and lawn sizes.',
    gradient: ['#43cea2', '#185a9d'],
  },
  {
    id: 'equipment',
    title: 'Lawn Equipment',
    icon: '‚öôÔ∏è',
    tagline: 'Power up your lawn game',
    description:
      'Edgers, aerators, dethatchers, and spreaders to keep your Bermuda turf in championship shape.',
    gradient: ['#f7971e', '#ffd200'],
  },
  {
    id: 'tools',
    title: 'Lawn Tools',
    icon: 'üõ†Ô∏è',
    tagline: 'The essentials in every shed',
    description:
      'Rakes, hoes, pruners, and hand tools for everyday Bermuda lawn care and maintenance tasks.',
    gradient: ['#e44d26', '#f16529'],
  },
  {
    id: 'fertilizers',
    title: 'Fertilizers',
    icon: 'üß™',
    tagline: 'Feed your turf right',
    description:
      'Nitrogen-rich blends, slow-release granules, and seasonal feeding schedules tailored for Bermuda grass.',
    gradient: ['#56ab2f', '#a8e063'],
  },
  {
    id: 'insecticides',
    title: 'Insecticides',
    icon: 'üêõ',
    tagline: 'Defend against grubs & pests',
    description:
      'Target armyworms, grubs, and chinch bugs with safe, effective insecticides made for warm-season turf.',
    gradient: ['#DA4453', '#89216B'],
  },
  {
    id: 'pesticides',
    title: 'Pesticides',
    icon: 'üõ°Ô∏è',
    tagline: 'Weed & disease control',
    description:
      'Pre-emergent & post-emergent herbicides plus fungicides to keep crabgrass and brown patch at bay.',
    gradient: ['#4776E6', '#8E54E9'],
  },
  {
    id: 'supplements',
    title: 'Supplements',
    icon: 'üíß',
    tagline: 'Boost soil & root health',
    description:
      'Iron supplements, humic acid, soil conditioners, and bio-stimulants for thick, dark-green Bermuda.',
    gradient: ['#00b4db', '#0083b0'],
  },
];

// ‚îÄ‚îÄ Animated card component ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
function CategoryCard({ item, index }) {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(40)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(fadeAnim, {
        toValue: 1,
        duration: 600,
        delay: index * 120,
        useNativeDriver: true,
      }),
      Animated.timing(slideAnim, {
        toValue: 0,
        duration: 600,
        delay: index * 120,
        useNativeDriver: true,
      }),
    ]).start();
  }, []);

  return (
    <Animated.View
      style={[
        styles.card,
        {
          opacity: fadeAnim,
          transform: [{ translateY: slideAnim }],
        },
      ]}
    >
      <LinearGradient
        colors={item.gradient}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
        style={styles.cardGradient}
      >
        <View style={styles.cardIconContainer}>
          <Text style={styles.cardIcon}>{item.icon}</Text>
        </View>
        <View style={styles.cardContent}>
          <Text style={styles.cardTitle}>{item.title}</Text>
          <Text style={styles.cardTagline}>{item.tagline}</Text>
        </View>
      </LinearGradient>
      <View style={styles.cardBody}>
        <Text style={styles.cardDescription}>{item.description}</Text>
        <View style={styles.cardFooter}>
          <Text style={styles.comingSoon}>Explore ‚Üí</Text>
        </View>
      </View>
    </Animated.View>
  );
}

// ‚îÄ‚îÄ Main App ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
export default function App() {
  const heroFade = useRef(new Animated.Value(0)).current;
  const heroScale = useRef(new Animated.Value(0.95)).current;

  useEffect(() => {
    Animated.parallel([
      Animated.timing(heroFade, {
        toValue: 1,
        duration: 800,
        useNativeDriver: true,
      }),
      Animated.spring(heroScale, {
        toValue: 1,
        friction: 8,
        tension: 40,
        useNativeDriver: true,
      }),
    ]).start();
  }, []);

  return (
    <View style={styles.container}>
      <StatusBar style="light" />
      <ScrollView
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
        showsVerticalScrollIndicator={false}
      >
        {/* ‚îÄ‚îÄ Hero Section ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
        <LinearGradient
          colors={['#0d4f1c', '#1a8c36', '#2ecc71']}
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 1 }}
          style={styles.hero}
        >
          <Animated.View
            style={[
              styles.heroContent,
              {
                opacity: heroFade,
                transform: [{ scale: heroScale }],
              },
            ]}
          >
            <Text style={styles.heroEmoji}>üå±</Text>
            <Text style={styles.heroTitle}>Bermuda{'\n'}Lawn Care</Text>
            <Text style={styles.heroSubtitle}>
              Your complete guide to a lush, healthy Bermuda lawn ‚Äî from mowing
              to feeding and everything in between.
            </Text>
            <View style={styles.heroBadge}>
              <Text style={styles.heroBadgeText}>
                üè° For Homeowners, By Experts
              </Text>
            </View>
          </Animated.View>

          {/* Decorative circles */}
          <View style={[styles.circle, styles.circle1]} />
          <View style={[styles.circle, styles.circle2]} />
          <View style={[styles.circle, styles.circle3]} />
        </LinearGradient>

        {/* ‚îÄ‚îÄ Section Header ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
        <View style={styles.sectionHeaderContainer}>
          <Text style={styles.sectionHeader}>Explore Categories</Text>
          <Text style={styles.sectionSubHeader}>
            Everything you need for a perfect Bermuda lawn
          </Text>
        </View>

        {/* ‚îÄ‚îÄ Category Cards ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
        <View style={styles.cardList}>
          {CATEGORIES.map((cat, i) => (
            <CategoryCard key={cat.id} item={cat} index={i} />
          ))}
        </View>

        {/* ‚îÄ‚îÄ Footer ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ */}
        <LinearGradient
          colors={['#0d4f1c', '#145a24']}
          style={styles.footer}
        >
          <Text style={styles.footerEmoji}>üåø</Text>
          <Text style={styles.footerText}>
            Bermuda Lawn Care ‚Äî More sections coming soon!
          </Text>
          <Text style={styles.footerSub}>Version 1.0</Text>
        </LinearGradient>
      </ScrollView>
    </View>
  );
}

// ‚îÄ‚îÄ Styles ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ‚îÄ
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f0f4f0',
  },
  scrollView: {
    flex: 1,
  },
  scrollContent: {
    paddingBottom: 0,
  },

  /* Hero */
  hero: {
    paddingTop: Platform.OS === 'ios' ? 60 : 48,
    paddingBottom: 48,
    paddingHorizontal: 24,
    borderBottomLeftRadius: 32,
    borderBottomRightRadius: 32,
    overflow: 'hidden',
    position: 'relative',
  },
  heroContent: {
    alignItems: 'center',
    zIndex: 2,
  },
  heroEmoji: {
    fontSize: 56,
    marginBottom: 12,
  },
  heroTitle: {
    fontSize: 38,
    fontWeight: '900',
    color: '#ffffff',
    textAlign: 'center',
    letterSpacing: 1,
    lineHeight: 44,
    textShadowColor: 'rgba(0,0,0,0.25)',
    textShadowOffset: { width: 0, height: 2 },
